import React from 'react';
import { TypeAnimation } from 'react-type-animation';
import styles from './Hero.module.css';

import hero from '../../assets/hero/heroImage.png';

export const Hero = () => {
    return (
        <section className={styles.container}>
            <div className={styles.content}>
                <h1 className={styles.title}>Hi! I'm Mohan Kumar</h1>
                <span className={styles.description}>
                    <TypeAnimation sequence={["I'm a Software Developer", 2000, "I'm a Full Stack Developer", 2000]}
                        speed={50} wrapper='span' repeat={Infinity} style={{ color: '#9c57bc' }} />
                    <p>with 2 years of experienceusing react & .Net. Reach out to learn more!</p>
                </span>
                <a href='mailto:mohanprojectid@gmail.com' className={styles.contactBtn}>Contact me</a>
            </div>
            <img src={hero} alt='hero img' className={styles.heroImg} />
            <div className={styles.topBlur} />
            <div className={styles.bottomBlur} />
        </section>
    )
}
