import React from 'react'
import styles from './Hero.module.css';

import hero from '../../assets/hero/heroImage.png';

export const Hero = () => {
    return (
        <section>
            <div>
                <h1>Hi! I am Mohan Kumar</h1>
                <p>I'm a full-stack developer with 2 years
                    of experienceusing react & .Net. Reach out to learn more!
                </p>
                <a href='mailto:mohanprojectid@gmail.com'>Contact me</a>
            </div>
            <img src={hero} alt='hero img' />
            <div className={styles.topBlur} />
            <div className={styles.BottomBlur} />
        </section>
    )
}
