import React from "react";
import styles from './Certification.module.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.min.css';
import 'swiper/swiper.min.css';
import SwiperCore, { Navigation, Pagination, Scrollbar, A11y } from 'swiper';

import az204 from '../../assets/certification/az204.svg';

// Install Swiper modules
SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);


export const Certification = () => {
  return (
    <section className={styles.container} id="Certification">
      <h2 className={styles.title}>Certifications</h2>
      <p />
      <Swiper
        spaceBetween={50}
        slidesPerView={3}
        navigation
        pagination={{ clickable: true }}
        scrollbar={{ draggable: true }}
        onSwiper={(swiper) => console.log(swiper)}
        onSlideChange={() => console.log('slide change')}
      >
        <SwiperSlide><img src={az204} alt="Image 1" /></SwiperSlide>
        <SwiperSlide><img src="image2.jpg" alt="Image 2" /></SwiperSlide>
        <SwiperSlide><img src="image3.jpg" alt="Image 3" /></SwiperSlide>
        <SwiperSlide><img src="image4.jpg" alt="Image 4" /></SwiperSlide>
        <SwiperSlide><img src="image5.jpg" alt="Image 5" /></SwiperSlide>
        <SwiperSlide><img src="image6.jpg" alt="Image 6" /></SwiperSlide>
        {/* Add more slides as needed */}
      </Swiper>
    </section>
  )
}
